public class Mostrarreal2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      /*
       * coge el valor de una variable de tipo real con decimales lo muestra por pantalla
       */
	double n;
	n = Math.PI;
	System.out.println("El valor de la variable es " + n);
	System.out.println("El valor de la variable es  %f", n);
	System.out.println("\nEl valor de la variable es %.2f", n);
	}

}
